import React, { useCallback, useEffect, useState } from "react";
import axios from "axios";
import { Sidebar } from "../component/Sidebar";
import { Header } from "../component/Header";
import { EditUserPopup } from "../component/EditUserpopup";
import { useAuth } from "../context/auth";
import { useLocation, useNavigate } from "react-router";

const DaftarUser = () => {
    const { token, logout, isLogin, isRole } = useAuth();
    const [user, setUser] = useState({});
    const [users, setUsers] = useState([]);
    const [allUser, setAllUser] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [error, setError] = useState(null);
    const [selectedUser, setSelectedUser] = useState(null);
    const navigate = useNavigate();
    const location = useLocation();


    const searchQueryFromUrl = new URLSearchParams(location.search).get('q');


    // Fetch user data
    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/user", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setUser(response.data);
        } catch (error) {
            setError(error.message);
        }
    }, [token]);

    // Fetch all users
    const getUsers = useCallback(async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/alluser", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setUsers(response.data);
            setAllUser(response.data);
        } catch (error) {
            setError(error.message);
        }
    }, [token]);

    // Delete a user
    const deleteUser = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/user/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            getUsers();
        } catch (error) {
            setError(error.message);
        }
    };

    // Search users
    const searchUser = (e) => {
        e.preventDefault();
        navigate(`/daftar-user-admin?q=${searchQuery}`);

        const filteredUsers = allUser.filter(user =>
            user.Username.toLowerCase().includes(searchQuery.toLowerCase()) ||
            user.Email.toLowerCase().includes(searchQuery.toLowerCase()) ||
            user.Nama.toLowerCase().includes(searchQuery.toLowerCase())
        );

        setUsers(filteredUsers);
    };

    // Save updated user data
    const saveUser = async (id, updatedData) => {
        try {
            await axios.put(`http://localhost:5000/api/user/${id}`, updatedData, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setSelectedUser(null);
            getUsers();
        } catch (error) {
            setError(error.message);
        }
    };

    // On component mount
    useEffect(() => {
        if (!isLogin || isRole !== "Admin") {
            navigate("/login");
            return;
        }
        fetchUser();
        getUsers();
        if (searchQueryFromUrl) {
            setSearchQuery(searchQueryFromUrl);
        }
    }, [fetchUser, getUsers, navigate, isLogin, isRole, searchQueryFromUrl]);

    return (
        <div className="flex flex-row bg-gray-50">
            <Sidebar />
            <div className="flex flex-1 flex-col h-screen">
                <Header user={user} logout={logout} />
                <div className='flex flex-row items-center justify-between mx-6 rounded-lg'>
                    <button className='bg-blue-500 text-white px-4 py-2 rounded-lg'>Tambah Buku</button>
                    <form className='flex flex-row gap-2' onSubmit={searchUser}>
                        <input
                            type="text"
                            placeholder="Search"
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            className="border p-2 rounded-lg"
                        />
                        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-lg">
                            Search
                        </button>
                    </form>
                </div>

                <div className="m-6 bg-white rounded-lg shadow-md max-w-full h-full overflow-hidden">
                    {error && <p className="text-red-500 text-center">Error: {error}</p>}
                    <div className="overflow-x-auto max-h-full">
                        <table className="min-w-full divide-y divide-gray-200">
                            <thead className="bg-blue-500 sticky top-0">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">No</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">Nama</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">Email</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">Role</th>
                                    <th className="px-6 py-3 text-center text-xs font-medium text-white uppercase tracking-wider">Aksi</th>
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {users.length > 0 ? (
                                    users.map((user, index) => (
                                        <tr key={user.UserID}>
                                            <td className="px-6 py-3 whitespace-nowrap text-sm">{index + 1}</td>
                                            <td className="px-6 py-3 whitespace-nowrap text-sm">{user.Username}</td>
                                            <td className="px-6 py-3 whitespace-nowrap text-sm">{user.Email}</td>
                                            <td className="px-6 py-3 whitespace-nowrap text-sm">{user.Role.RoleName}</td>
                                            <td className="items-center justify-center">
                                                <div className="gap-2 flex flex-row">
                                                    <button className="py-1 px-2 border-2 border-yellow-500 text-sm text-yellow-500 rounded-xl" onClick={() => setSelectedUser(user)}>Edit</button>
                                                    <button className="py-1 px-2 border-2 border-red-500 text-sm text-red-500 rounded-xl" onClick={() => deleteUser(user.UserID)}>Hapus</button>
                                                </div>
                                            </td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr>
                                        <td colSpan="5" className="text-center py-3 text-gray-500">Tidak ada data user.</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>

                {selectedUser && (
                    <EditUserPopup
                        user={selectedUser}
                        onClose={() => setSelectedUser(null)}
                        onSave={saveUser}
                    />
                )}
            </div>
        </div>
    );
};

export default DaftarUser;
