import React, { useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/auth.js';  
import { Sidebar } from '../component/Sidebar.js';
import { useNavigate } from 'react-router-dom';  

const DaftarBuku = () => {
    const { token, login, isLogin, isRole, logout } = useAuth(); 
    const [user, setUser] = useState({});
    const [buku, setBuku] = useState([]);
    
    const navigate = useNavigate();
         useEffect(() => {
        if (!isLogin) {
            navigate('/login');  
        }
    }, [isLogin, navigate]);

    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/user', {
                headers: {
                    Authorization: `Bearer ${token}` 
                },
            });
            setUser(response.data); 
        } catch (error) {
            console.error('Error fetching user:', error.message);
        }
    }, [token]);
    
    const deleteBuku = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/buku/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}` 
                },
            });
            deleteBuku(); 
        } catch (error) {
            console.error('Error deleting book:', error.message);
        }
    }

    const fetchBuku = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/buku', {
                headers: {
                    Authorization : `Bearer ${token}`
                }
            });
            setBuku(response.data);
        } catch (error) {
            console.error('Error fetching books:', error.message);
        }
    }, [token]);

    useEffect(() => {
        if (isLogin) {
            fetchUser(); 
            fetchBuku();
        }
    }, [isLogin, fetchUser, fetchBuku]);

    return (
        <div className='flex flex-row h-screen w-screen '>
            <Sidebar />
            <div className="flex flex-col overflow-y-auto flex-1 bg-blue-100">
                <div className="flex items-center justify-between rounded-lg m-4 p-4 bg-white shadow-md">
                    <div className="flex flex-col text-2xl font-semibold text-gray-800">
                        {user.Username}
                        <span className='text-gray-500 text-sm'>{user.Email}</span>
                    </div>
                    <button onClick={logout} className="px-4 py-2 bg-red-500 text-white rounded-lg">
                        Logout
                    </button>
                </div>
                <div className="flex-1 p-6 m-4 rounded-lg shadow-md bg-gray-50">
                    <Link to="/add-buku" className='py-2 px-4 bg-green-500 text-white rounded-lg'>Tambah Buku</Link>
                    {buku.length > 0 ? (
                        <table className="table-fixed w-full">
                            <thead>
                                <tr>
                                    <th className="text-lg px-4 py-2 w-20">BukuID</th>
                                    <th className="text-lg px-4 py-2">Judul</th>
                                    <th className="text-lg px-4 py-2">Penulis</th>
                                    <th className="text-lg px-4 py-2 w3">Penerbit</th>
                                    <th className="text-lg px-4 py-2 w-20">Tahun</th>
                                    <th className="text-lg px-4 py-2 w-20"></th>
                                    <th className="text-lg px-4 py-2 w-28"></th>
                                </tr>
                            </thead>
                            <tbody>
                                {buku.map((book) => (
                                    <tr key={book.ID}>
                                        <td className="border px-4 py-2">{book.BukuID}</td>
                                        <td className="border px-4 py-2">{book.Judul}</td>
                                        <td className="border px-4 py-2">{book.Penulis}</td>
                                        <td className="border px-4 py-2">{book.Penerbit}</td>
                                        <td className="border px-4 py-2">{book.TahunTerbit}</td>
                                        <td className='border text-l font-semibold px-4 py-2'><button className='py-2 px-4 bg-yellow-500 text-white rounded-lg'>Edit</button></td>
                                        <td className='border text-l font-semibold px-4 py-2'><button className='py-2 px-4 bg-red-500 text-white rounded-lg' onClick={() => deleteBuku(book.BukuID)}>Hapus</button></td>

                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p className="text-gray-600 text-center">Tidak ada data buku tersedia.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default DaftarBuku;
