import React, { useCallback, useEffect, useState } from "react";
import axios from "axios";
import { Sidebar } from "../component/Sidebar";
import { Header } from "../component/Header";
import { useAuth } from "../context/auth";

const DaftarUser = () => {
    const { token, logout, isLogin, isRole } = useAuth();
    const [user, setUser] = useState({});
    const [users, setUsers] = useState([]);
    const [error, setError] = useState("");

    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/user", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setUser(response.data);
        } catch (error) {
            setError(error.message);
        }
    }, [token]);

    const getUsers = useCallback(async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/alluser", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setUsers(response.data);
        } catch (error) {
            setError(error.message);
        }
    }, [token]);

    useEffect(() => {
        if (!isLogin) {
            logout();
        } else if (isRole !== "Admin") {
            logout();
        } else {
            fetchUser();
            getUsers();
        }
    }, [fetchUser, getUsers, isLogin, isRole, logout]);

    return (
        <div className="flex flex-row bg-gray-50">
            <Sidebar />
            <div className="flex-1 flex-col ">
                <Header user={user} logout={logout} />
                <div className="m-6 p-6 bg-white rounded-lg shadow-md">
                    <h1 className="text-2xl font-semibold mb-4">Daftar User</h1>
                    {error && <div className="text-red-500">{error}</div>}
                    {users.length > 0 ? (    
                    <table className="min-w-full divide-y divide-gray-200">
                        <thead className="bg-blue-200">
                            <tr>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                    No
                                </th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                    Nama
                                </th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                    Email
                                </th>
                            </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                            {users.map((user) => (
                                <tr key={user.id}>
                                    <td className="px-6 py-4 whitespace-nowrap">
                                        {user.Username}
                                    </td>
                                    <td className="px-6 py-4 whitespace-nowrap">
                                        {user.Email}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    ) : (
                        <p>Tidak ada data user.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default DaftarUser;
