import React, { useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
import { useAuth } from '../context/auth.js'; // Import AuthContext
import { Sidebar } from '../component/Sidebar.js';

const Dashboard = () => {
    const { token, isLogin, isRole, logout } = useAuth(); // Gunakan AuthContext
    const [user, setUser] = useState({});
    const [buku, setBuku] = useState([]);
    const [loadingUser, setLoadingUser] = useState(true);
    const [loadingBuku, setLoadingBuku] = useState(true);

    // Redirect jika tidak login
    useEffect(() => {
        if (!isLogin) {
            // window.location.href = '/login'; // Uncomment untuk redirect ke halaman login
        }
    }, [isLogin]);

    const fetchUser = useCallback(async () => {
        if (!token) return;
        setLoadingUser(true);
        try {
            const decodedToken = jwt_decode(token);
            const { UserID } = decodedToken;

            const response = await axios.get(`http://localhost:5000/api/user/${UserID}`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });
            setUser(response.data);
        } catch (error) {
            console.error('Error fetching user:', error.message);
        } finally {
            setLoadingUser(false);
        }
    }, [token]);

    const fetchBuku = useCallback(async () => {
        if (!token) return;
        setLoadingBuku(true);
        try {
            const response = await axios.get('http://localhost:5000/api/buku', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });
            setBuku(response.data);
        } catch (error) {
            console.error('Error fetching books:', error.message);
        } finally {
            setLoadingBuku(false);
        }
    }, [token]);

    useEffect(() => {
        fetchUser();
        fetchBuku();
    }, [fetchUser, fetchBuku]);

    return (
        <div className='flex flex-row h-screen w-screen '>
            <Sidebar />
            <div className="flex flex-col flex-1 bg-blue-100">
                <div className="flex items-center justify-between rounded-lg m-6 p-4 bg-white shadow-md">
                    {loadingUser ? (
                        <div className="text-2xl font-semibold text-gray-800">Loading...</div>
                    ) : (
                        <>
                            <div className="text-2xl font-semibold text-gray-800">{user.Username}</div>
                            <div className="text-sm font-medium text-gray-500">Role: {isRole}</div>
                            <button onClick={logout} className="px-4 py-2 bg-red-500 text-white rounded">
                                Logout
                            </button>
                        </>
                    )}
                </div>
                <div className="flex-1 p-6 bg-gray-50">
                    {loadingBuku ? (
                        <div className="text-center text-gray-600">Loading data buku...</div>
                    ) : (
                        <table className="table-fixed w-full">
                            <thead>
                                <tr>
                                    <th className="px-4 py-2">Judul</th>
                                    <th className="px-4 py-2">Pengarang</th>
                                    <th className="px-4 py-2">Tahun</th>
                                </tr>
                            </thead>
                            <tbody>
                                {buku.map((book) => (
                                    <tr key={book.ID}>
                                        <td className="border px-4 py-2">{book.Judul}</td>
                                        <td className="border px-4 py-2">{book.Pengarang}</td>
                                        <td className="border px-4 py-2">{book.Tahun}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
