import React, { useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/auth.js';  
import { Sidebar } from '../component/Sidebar.js';
import { useNavigate } from 'react-router-dom';  

const DaftarBuku = () => {
    const { token, login, isLogin, isRole, logout } = useAuth(); 
    const [user, setUser] = useState({});
    const [searchQuery, setSearchQuery] = useState('');
    const [buku, setBuku] = useState([]);
    const [filteredBuku, setFilteredBuku] = useState([]);  // State untuk menyimpan hasil pencarian
    
    const navigate = useNavigate();
    
    useEffect(() => {
        if (!isLogin) {
            navigate('/login');  
        }
    }, [isLogin, navigate]);

    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/user', {
                headers: {
                    Authorization: `Bearer ${token}` 
                },
            });
            setUser(response.data); 
        } catch (error) {
            console.error('Error fetching user:', error.message);
        }
    }, [token]);

    const seachBuku = async (e) => {
        e.preventDefault(); // Mencegah form submit default
        try {
            const response = await axios.get(`http://localhost:5000/api/buku/search?q=${searchQuery}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                },
            });
            setFilteredBuku(response.data);  // Update filteredBuku dengan hasil pencarian
        } catch (error) {
            console.error('Error searching books:', error.message);
        }
    };
    
    const deleteBuku = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/buku/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}` 
                },
            });
            fetchBuku(); // Ambil ulang data setelah buku dihapus
        } catch (error) {
            console.error('Error deleting book:', error.message);
        }
    }

    const fetchBuku = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/buku', {
                headers: {
                    Authorization : `Bearer ${token}`
                }
            });
            setBuku(response.data); // Update buku dengan data dari backend
        } catch (error) {
            console.error('Error fetching books:', error.message);
        }
    }, [token]);

    useEffect(() => {
        if (isLogin) {
            fetchUser(); 
            fetchBuku();
        }
    }, [isLogin, fetchUser, fetchBuku]);

    const booksToDisplay = searchQuery ? filteredBuku : buku;  // Tentukan apakah menampilkan buku hasil search atau semua buku

    return (
        <div className='flex flex-row h-screen w-screen '>
            <Sidebar />
            <div className="flex flex-col overflow-y-auto flex-1 bg-blue-100">
                <div className="flex items-center justify-between rounded-lg m-4 p-4 bg-white shadow-md">
                    <form onSubmit={seachBuku}>
                        <input 
                            type="text" 
                            placeholder="Search" 
                            value={searchQuery} 
                            onChange={(e) => setSearchQuery(e.target.value)} // Update searchQuery
                            className="border p-2 rounded w-full"
                        />
                        <button type="submit" className="bg-blue-500 text-white px-4 py-2 mt-2 rounded">
                            Search
                        </button>
                    </form>
                    <button onClick={logout} className="px-4 py-2 bg-red-500 text-white rounded-lg">
                        Logout
                    </button>
                </div>
                <div className="flex-1 flex-col p-6 m-4 rounded-lg shadow-md bg-gray-50">
                    <Link to="/add-buku" className='py-2 px-4 bg-green-500 text-white rounded-lg'>Tambah Buku</Link>
                    {booksToDisplay.length > 0 ? (
                        <table className="table-none table-fixed w-full">
                            <thead className="bg-gray-200 rounded-lg">
                                <tr>
                                    <th className="text-lg border-gray-600 border px-4 py-2 ">BukuID</th>
                                    <th className="text-lg border-gray-600 border px-4 py-2">Judul</th>
                                    <th className="text-lg border-gray-600 border px-4 py-2">Penulis</th>
                                    <th className="text-lg border-gray-600 border px-4 py-2 ">Penerbit</th>
                                    <th className="text-lg border-gray-600 border px-4 py-2 ">Tahun</th>
                                    <th className="text-lg border-gray-600 border px-4 py-2 "></th>
                                    <th className="text-lg border-gray-600 border px-4 py-2 "></th>
                                </tr>
                            </thead>
                            <tbody>
                                {booksToDisplay.map((book) => (
                                    <tr key={book.ID}>
                                        <td className="border-gray-600 border px-4 py-2">{book.BukuID}</td>
                                        <td className="border-gray-600 border px-4 py-2">{book.Judul}</td>
                                        <td className="border-gray-600 border px-4 py-2">{book.Penulis}</td>
                                        <td className="border-gray-600 border px-4 py-2">{book.Penerbit}</td>
                                        <td className="border-gray-600 border px-4 py-2">{book.TahunTerbit}</td>
                                        <td className='border-gray-600 border text-l font-semibold px-4 py-2'>
                                            <button className='py-2 px-4 bg-yellow-500 text-white rounded-lg'>Edit</button>
                                        </td>
                                        <td className='border-gray-600 border text-l font-semibold px-4 py-2'>
                                            <button className='py-2 px-4 bg-red-500 text-white rounded-lg' onClick={() => deleteBuku(book.BukuID)}>Hapus</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p className="text-gray-600 text-center">Tidak ada data buku tersedia.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default DaftarBuku;
