import React, { useState } from 'react';



const Popup = ({ title, onCancel, onSubmit }) => {
    const [judulbuku, setjudulbuku] = useState('');
    const [penulis, setpenulis] = useState('');
    const [penerbit, setpenerbit] = useState('');
    const [tahunterbit, settahunterbit] = useState('');

    const addBuku = (e) => {
        e.preventDefault();
        
    };
    return (
    <div className="fixed inset-0 bg-gray-900 bg-opacity-50 flex justify-center items-center">
      <div className="bg-gray-100 rounded-md shadow-lg w-96">
        <h2 className="text-xl font-bold text-center  bg-blue-500 text-white py-2 rounded-t-md">
          Tambah Buku
        </h2><form>
        <div className="p-6">
            <input
              type="text"
              placeholder="Judul Buku"
              className="w-full font-semibold p-1 bg-blue-100 border-2 border-gray-400 rounded-lg mb-4"
            />
            <input
              type="text"
              placeholder="Penulis"
              className="w-full font-semibold p-1 bg-blue-100 border-2 border-gray-400 rounded-lg mb-4"
            />
            <input
              type="text"
              placeholder="Penerbit"
              className="w-full font-semibold p-1 bg-blue-100 border-2 border-gray-400 rounded-lg mb-4"
            />
            <input
              type="number"
              placeholder="Tahun Terbit"
              className="w-full font-semibold p-1 bg-blue-100 border-2 border-gray-400 rounded-lg mb-4"
            />
            
        <div className="flex justify-between pt-3">
          <button
            onClick={onCancel}
            className=" border-2 border-blue-400 text-black px-12 font-semibold rounded-lg hover:bg-gray-300"
          >
            Kembali
          </button>
          <button
            onClick={onSubmit}
            className=" border-2 border-green-400 text-black px-12 font-semibold rounded-lg hover:bg-green-600"
          >
           Tambah
          </button>
        </div>
        </div>
        </form>
      </div>
    </div>
  );
};


export default Popup;
