import React, { useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import { Sidebar } from '../component/Sidebar';
import { useAuth } from '../context/auth';

const DaftarUser = () => {
    const { token, logout, isLogin, isRole } = useAuth();
    const [users, setUsers] = useState([]);
    const [error, setError] = useState('');

    const fetchUsers = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/user', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setUsers(response.data);
        } catch (error) {
            setError(error.message);
        }
    }, [token]);

    useEffect(() => {
        fetchUsers();
    }, [fetchUsers]);

    return (
        <div className='flex flex-row'>
            <Sidebar />
            <div className='flex flex-col w-full'>
                <div className="flex items-center justify-between rounded-lg m-6 p-4 bg-white shadow-md">
                    <h2 className='text-2xl font-semibold text-gray-800'>Daftar User</h2>
                    {isLogin && (
                        <button onClick={logout} className="px-4 py-2 bg-red-500 text-white rounded">
                            Logout
                        </button>
                    )}
                </div>
                {error && <p className='text-red-500'>{error}</p>}
                <table className='table-auto w-full'>
                    <thead>
                        <tr>
                            <th className='px-4 py-2'>ID</th>
                            <th className='px-4 py-2'>Username</th>
                            <th className='px-4 py-2'>Email</th>
                            <th className='px-4 py-2'>Role</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user) => (
                            <tr key={user.ID}>
                                <td className='px-4 py-2'>{user.ID}</td>
                                <td className='px-4 py-2'>{user.Username}</td>
                                <td className='px-4 py-2'>{user.Email}</td>
                                <td className='px-4 py-2'>{user.Role}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default DaftarUser;
