import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/auth";

export const DetailBukuPopup = ({ onClose, buku, userId }) => {
    const { token } = useAuth
    const [formData, setFormData] = useState({
        BukuID: "",
        Judul: "",
        Penulis: "",
        Penerbit: "",
        TahunTerbit: "",
        Stok: "",
        KategoriID: "",
        Ulasan: "",
        Rating: 0,
    });

    useEffect(() => {
        if (buku) {
            setFormData(buku);
        }
    }, [buku]);

    const handleUlasanChange = (e) => {
        setFormData({
            ...formData,
            Ulasan: e.target.value,
        });
    };

    const handleRatingChange = (e) => {
        setFormData({
            ...formData,
            Rating: parseInt(e.target.value, 10),
        });
    };

    const handlePinjamBuku = async () => {
        try {
            const payload = {
                PeminjamanID: Date.now(),
                BukuID: formData.BukuID,
                UserID: userId,
                TanggalPeminjaman: new Date().toISOString(),
                TanggalPengembalian: null,
                StatusPeminjaman: "Dipinjam",
            };

            await axios.post("http://localhost:5000//api/peminjaman", payload);
            alert(`Buku \"${formData.Judul}\" berhasil dipinjam.`);
            onClose();
        } catch (error) {
            console.error("Gagal meminjam buku:", error);
            alert("Terjadi kesalahan saat meminjam buku.");
        }
    };

    const handleKirimUlasan = async () => {
        try {
            const payload = {
                UlasanID: Date.now(),
                UserID: userId,
                BukuID: formData.BukuID,
                Ulasan: formData.Ulasan,
                Rating: formData.Rating,
            };

            await axios.post("/api/ulasan", payload);
            alert("Ulasan berhasil dikirim.");
            onClose();
        } catch (error) {
            console.error("Gagal mengirim ulasan:", error);
            alert("Terjadi kesalahan saat mengirim ulasan.");
        }
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50" onClick={onClose}>
            <div className="bg-white p-6 rounded-lg shadow-md w-full max-w-lg" onClick={(e) => e.stopPropagation()}>
                <h2 className="text-xl font-semibold text-gray-800 mb-4">Detail Buku</h2>
                <div className="mb-4">
                    <p><strong>ID Buku:</strong> {formData.BukuID}</p>
                    <p><strong>Judul:</strong> {formData.Judul}</p>
                    <p><strong>Penulis:</strong> {formData.Penulis}</p>
                    <p><strong>Penerbit:</strong> {formData.Penerbit}</p>
                    <p><strong>Tahun Terbit:</strong> {formData.TahunTerbit}</p>
                    <p><strong>Stok:</strong> {formData.Stok}</p>
                </div>
                <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Ulasan</label>
                    <textarea
                        value={formData.Ulasan}
                        onChange={handleUlasanChange}
                        className="w-full px-4 py-2 border rounded-lg focus:outline-none"
                        placeholder="Tulis ulasan Anda di sini"
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Rating</label>
                    <select
                        value={formData.Rating}
                        onChange={handleRatingChange}
                        className="w-full px-4 py-2 border rounded-lg focus:outline-none"
                    >
                        <option value={0}>Pilih Rating</option>
                        {[1, 2, 3, 4, 5].map((bintang) => (
                            <option key={bintang} value={bintang}>{bintang} Bintang</option>
                        ))}
                    </select>
                </div>
                <div className="flex justify-between">
                    <button
                        onClick={handlePinjamBuku}
                        className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                    >
                        Pinjam Buku
                    </button>
                    <button
                        onClick={handleKirimUlasan}
                        className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
                    >
                        Kirim Ulasan
                    </button>
                    <button
                        onClick={onClose}
                        className="px-4 py-2 bg-gray-300 text-gray-700 rounded-lg hover:bg-gray-400"
                    >
                        Tutup
                    </button>
                </div>
            </div>
        </div>
    );
};
