import React, { useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/auth.js';
import { Sidebar } from '../component/Sidebar.js';
import { Header } from '../component/Header.js';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
    const { token, isLogin, isRole, logout } = useAuth();
    const [user, setUser] = useState({});
    const [counts, setCounts] = useState({
        countUser: 0,
        countBorrow: 0,
        countBuku: 0,
        countStok: 0,
    });
    const navigate = useNavigate;

    const fetchCounts = useCallback(async () => {
        try {
            const response = await Promise.all([
                axios.get('http://localhost:5000/api/countuser', {
                    headers: { Authorization: `Bearer ${token}` }
                }),
                axios.get('http://localhost:5000/api/countborrow', {
                    headers: { Authorization: `Bearer ${token}` }
                }),
                axios.get('http://localhost:5000/api/countbuku', {
                    headers: { Authorization: `Bearer ${token}` }
                }),
                axios.get('http://localhost:5000/api/countstok', {
                    headers: { Authorization: `Bearer ${token}` }
                }),
            ]);
            setCounts({
                countUser: response[0].data.count || 0,
                countBorrow: response[1].data.count || 0,
                countBuku: response[2].data.count || 0,
                countStok: response[3].data.count || 0,
            });
        } catch (error) {
            console.error('Error fetching counts:', error.message);
        }
    }, [token]);

    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/user', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setUser(response.data);
        } catch (error) {
            console.error('Error fetching user:', error.message);
        }
    }, [token]);

    useEffect(() => {
        if (!isLogin || isRole !== 'Admin') {
            navigate('/login');
            return;
        } 
        fetchUser();
        fetchCounts();
    }, [fetchUser, fetchCounts, isLogin, isRole, navigate]);

    return (
        <div className='flex flex-row h-screen w-screen'>
            <Sidebar />
            <div className="flex flex-col flex-1 bg-gray-50">
                <Header user={user} logout={logout} />
                <div className="flex flex-col p-6 gap-4 ">
                    <div className='flex flex-row justify-between gap-4'>
                        <div className="flex flex-col items-start bg-white shadow rounded-lg p-4 w-1/4">
                            <p className="text-gray-800 font-medium">Jumlah Pengguna: </p>
                            <span className='text-2xl font-semibold'>{counts.countUser}</span>
                        </div>
                        <div className="flex flex-col items-start bg-white shadow rounded-lg p-4 w-1/4">
                            <p className="text-gray-800 font-medium">Jumlah Peminjaman: </p>
                            <span className='text-2xl font-semibold'>{counts.countBorrow}</span>
                        </div>
                        <div className="flex flex-col items-start bg-white shadow rounded-lg p-4 w-1/4">
                            <p className="text-gray-800 font-medium">Jumlah Buku: </p>
                            <span className='text-2xl font-semibold'>{counts.countBuku}</span>
                        </div>
                        <div className="flex flex-col items-start bg-white shadow rounded-lg p-4 w-1/4">
                            <p className="text-gray-800 font-medium">Jumlah Total Stok Buku: </p>
                            <span className='text-2xl font-semibold'>{counts.countStok}</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
