import React, { useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import { Sidebar } from '../component/Sidebar';
import { useAuth } from '../context/auth';
import { useNavigate } from 'react-router-dom';  // Import useNavigate untuk redirect

const Peminjaman = () => {
    const { token, isLogin, logout } = useAuth();
    const [user, setUser] = useState({});
    const [borrowData, setBorrowData] = useState([]);
    const [loading, setLoading] = useState(true);  // Menambahkan state loading
    const [error, setError] = useState('');  // Menambahkan state error
    const navigate = useNavigate();  // Hook untuk navigasi

    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/user', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setUser(response.data);
        } catch (error) {
            setError('Error fetching user: ' + error.message);
        }
    }, [token]);

    const fetchBorrow = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/borrow', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setBorrowData(response.data);
        } catch (error) {
            setError('Error fetching borrow data: ' + error.message);
        } 
    }, [token]);

    useEffect(() => {
        if (!isLogin) {
            navigate('/login');  // Redirect menggunakan react-router
        } else {
            fetchUser();
            fetchBorrow();
        }
    }, [isLogin, navigate, fetchUser, fetchBorrow]);

    if (loading) {
        return <div>Loading...</div>;  // Tampilkan loading ketika data sedang diambil
    }

    return (
        <div className='flex flex-row h-screen w-screen'>
            <Sidebar />
            <div className='flex-1 flex-col bg-gray-50'>
                <div className="flex items-center justify-between rounded-lg m-6 p-4 bg-white shadow-md">
                    <div className="flex flex-col text-2xl font-semibold text-gray-800">
                        {user?.Username || 'Loading...'}
                        <span className='text-gray-500 text-sm'>{user?.Email || ''}</span>
                    </div>
                    {isLogin && (
                        <button onClick={logout} className="px-4 py-2 bg-red-500 text-white rounded">
                            Logout
                        </button>
                    )}
                </div>
                <div className="m-6 p-4 bg-white shadow-md rounded-lg">
                    <h2 className="text-xl font-bold mb-4">Borrowed Books</h2>

                    {error && <p className="text-red-500">{error}</p>}  {/* Menampilkan pesan error jika ada */}

                    {borrowData.length > 0 ? (
                        <table className="table-fixed w-full">
                            <thead>
                                <tr>
                                    <th className="px-4 py-2">ID</th>
                                    <th className="px-4 py-2">UserID</th>
                                    <th className="px-4 py-2">BukuID</th>
                                    <th className="px-4 py-2">Tanggal Peminjaman</th>
                                    <th className="px-4 py-2">Tanggal Pengembalian</th>
                                    <th className="px-4 py-2">Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {borrowData.map((peminjaman) => (
                                    <tr key={peminjaman.ID}>
                                        <td className="border px-4 py-2">{peminjaman.PeminjamanID}</td>
                                        <td className="border px-4 py-2">{peminjaman.UserID}</td>
                                        <td className="border px-4 py-2">{peminjaman.BukuID}</td>
                                        <td className="border px-4 py-2">{peminjaman.TanggalPeminjaman}</td>
                                        <td className="border px-4 py-2">{peminjaman.TanggalPengembalian}</td>
                                        <td className="border px-4 py-2">{peminjaman.StatusPeminjaman}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p className="text-gray-600 text-center p-4">
                            Tidak ada data borrowData tersedia.
                        </p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Peminjaman;
