import React from 'react';

const EditBukupopup = ({ buku, onClose, onSave }) => {
    const [formData, setFormData] = React.useState({
        Judul: buku?.Judul || "",
        Penerbit: buku?.Penerbit || "",
        Penulis: buku?.Penulis || "",
        TahunTerbit: buku?.TahunTerbit || "",
        Kategori: buku?.Kategori || "",
        Stok: buku?.Stok || "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(buku.BukuID, formData);
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                <h2 className="text-xl font-bold mb-4">Edit Buku</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Judul</label>
                        <input
                            type="text"
                            name="Judul"
                            value={formData.Judul}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Penerbit</label>
                        <input
                            type="text"
                            name="Penerbit"
                            value={formData.Penerbit}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Penulis</label>
                        <input
                            type="text"
                            name="Penulis"
                            value={formData.Penulis}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Tahun Terbit</label>
                        <input
                            type="number"
                            name="TahunTerbit"
                            value={formData.TahunTerbit}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Kategori</label>
                        <input
                            type="text"
                            name="Kategori"
                            value={formData.Kategori}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Stok</label>
                        <input
                            type="number"
                            name="Stok"
                            value={formData.Stok}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                        />
                    </div>
                    <div className="flex justify-end gap-2">
                        <button
                            type="button"
                            onClick={onClose}
                            className="bg-gray-500 text-white px-4 py-2 rounded-lg"
                        >
                            Batal
                        </button>
                        <button
                            type="submit"
                            className="bg-blue-500 text-white px-4 py-2 rounded-lg"
                        >
                            Simpan
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};
