import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Admin } from '../lib/navigation';

export const Sidebar = () => {
    return (
        <div className="hidden md:flex md:flex-col text-white shadow-[0_3px_6px_3px_rgba(0,0,0,0.3)] bg-white">
            <div className="flex flex-col items-center w-full mt-10 mb-5 h-2 bg-whit">
                <div className="flex-1 flex-col h-12">
                    {Admin.map((item, index) => (
                        <SidebarAnchor key={index} item={item} />
                    ))}
                </div>
            </div>
        </div>
    );
};

function SidebarAnchor({ item }) {
    const {pathname} = useLocation();
    return (
        <Link
      to={item.path} // Menggunakan item.path untuk URL
      className={`flex items-center px-4 py-2 text-black hover:bg-white hover:text-gray-900 ${
        pathname === item.path // Bandingkan pathname dengan item.path
          ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white'
          : ''
      }`}
    >
      <img src={item.icon} alt={`${item.label} icon`} className="w-6 h-6" />
      <span className="ml-4">{item.label}</span>
    </Link>
  );
}
