import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Sidebar } from '../component/Sidebar';
import { useAuth } from '../context/auth';

const AkunUser = () => {
    const { token, isLogin, logout, isRole } = useAuth();
    const [user, setUser] = useState({
        id: '',
        username: '',
        nama: '',
        email: '',
        telepon: '',
        password: '',
        passwordVerifikasi: ''
    });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const navigate = useNavigate();

    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/user', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setUser(response.data);
        } catch (error) {
            setError('Error fetching user: ' + error.message);
        }
    }, [token]);

    const handleUpdate = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');

        if (user.password !== user.passwordVerifikasi) {
            setError('Password dan verifikasi password tidak cocok.');
            return;
        }

        try {
            const response = await axios.put('http://localhost:5000/api/user', user, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setSuccess('Profil berhasil diperbarui!');
        } catch (error) {
            setError('Error updating profile: ' + error.message);
        }
    };

    useEffect(() => {
        if (!isLogin || isRole !== "User") {
            return navigate('/login');
        }
        fetchUser();
    }, [isLogin, isRole, navigate, fetchUser]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser((prevUser) => ({ ...prevUser, [name]: value }));
    };

    return (
        <div className='flex flex-row h'>
            <Sidebar />
            <div className='flex flex-1 flex-col bg-gray-50'>
                <div className="m-6 bg-white w-full p-6 shadow-md rounded-lg">
                    <h2 className="text-xl font-semibold mb-4">Profil Akun</h2>

                    {error && <div className="text-red-500 mb-4">{error}</div>}
                    {success && <div className="text-green-500 mb-4">{success}</div>}

                    <form onSubmit={handleUpdate}>
                        <div className="mb-4">
                            <label className="block text-gray-700">User ID</label>
                            <input
                                type="text"
                                name="id"
                                value={user.id}
                                disabled
                                className="w-full px-4 py-2 border rounded-lg bg-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>

                        <div className="mb-4">
                            <label className="block text-gray-700">Username</label>
                            <input
                                type="text"
                                name="username"
                                value={user.username}
                                onChange={handleChange}
                                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>

                        <div className="mb-4">
                            <label className="block text-gray-700">Nama Lengkap</label>
                            <input
                                type="text"
                                name="nama"
                                value={user.nama}
                                onChange={handleChange}
                                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>

                        <div className="mb-4">
                            <label className="block text-gray-700">Email</label>
                            <input
                                type="email"
                                name="email"
                                value={user.email}
                                onChange={handleChange}
                                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>

                        <div className="mb-4">
                            <label className="block text-gray-700">Telepon</label>
                            <input
                                type="text"
                                name="telepon"
                                value={user.telepon}
                                onChange={handleChange}
                                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>

                        <div className="mb-4">
                            <label className="block text-gray-700">Password</label>
                            <input
                                type="password"
                                name="password"
                                value={user.password}
                                onChange={handleChange}
                                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>

                        <div className="mb-4">
                            <label className="block text-gray-700">Verifikasi Password</label>
                            <input
                                type="password"
                                name="passwordVerifikasi"
                                value={user.passwordVerifikasi}
                                onChange={handleChange}
                                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>

                        <button
                            type="submit"
                            className="w-full bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
                        >
                            Update Profil
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default AkunUser;
