import React, { useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import { Sidebar } from '../component/Sidebar';
import { useAuth } from '../context/auth';
import { useNavigate } from 'react-router-dom';  // Import useNavigate untuk redirect

const Peminjaman = () => {
    const { token, isLogin, logout } = useAuth();
    const [user, setUser] = useState({});
    const [borrowData, setBorrowData] = useState([]);
    const [error, setError] = useState(''); 
    const navigate = useNavigate(); 

    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/user', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setUser(response.data);
        } catch (error) {
            setError('Error fetching user: ' + error.message);
        }
    }, [token]);

    const fetchBorrow = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/borrow', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setBorrowData(response.data);
        } catch (error) {
            setError('Error fetching borrow data: ' + error.message);
        } 
    }, [token]);

    useEffect(() => {
        if (!isLogin) {
            return navigate('/login');
        } 
        fetchUser();
        fetchBorrow();
    }, [isLogin, navigate, fetchUser, fetchBorrow]);

    return (
        <div className='flex flex-row h-screen w-screen'>
            <Sidebar />
            <div className='flex-1 flex-col bg-gray-50'>
                <He />
                <div className="m-6 bg-white shadow-md rounded-lg max-w-full h-full overflow-hidden">
                    {borrowData.length > 0 ? (
                        <table className="table-none table-fixed w-full">
                            <thead className='bg-blue-500 sticky top-0'>
                                <tr>
                                    <th className="px-6 py-3 text-xs text-left font-medium uppercase tracking-wider">No</th>
                                    <th className="px-6 py-3 text-xs text-left font-medium uppercase tracking-wider">UserID</th>
                                    <th className="px-6 py-3 text-xs text-left font-medium uppercase tracking-wider">BukuID</th>
                                    <th className="px-6 py-3 text-xs text-left font-medium uppercase tracking-wider">Tanggal Peminjaman</th>
                                    <th className="px-6 py-3 text-xs text-left font-medium uppercase tracking-wider">Tanggal Pengembalian</th>
                                    <th className="px-6 py-3 text-xs text-left font-medium uppercase tracking-wider">Status</th>
                                </tr>
                            </thead>
                            <tbody className='bg-white divide-y divide-gray-200'>
                                {borrowData.map((peminjaman, index) => (
                                    <tr key={peminjaman.ID}>
                                        <td className="px-6 py-3 whitespace-nowrap">{index + 1}</td>
                                        <td className="px-6 py-3 whitespace-nowrap">{peminjaman.User.Username}</td>
                                        <td className="px-6 py-3 whitespace-nowrap">{peminjaman.Buku.Judul}</td>
                                        <td className="px-6 py-3 whitespace-nowrap">{peminjaman.TanggalPeminjaman}</td>
                                        <td className="px-6 py-3 whitespace-nowrap">{peminjaman.TanggalPengembalian ?(peminjaman.TanggalPengembalian) : '-'}</td>
                                        <td className="px-6 py-3 whitespace-nowrap">{peminjaman.StatusPeminjaman}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p className="text-gray-600 text-center p-4">
                            Tidak ada data peminjaman tersedia.
                        </p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Peminjaman;
