import React, { useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/auth.js';
import { Sidebar } from '../component/Sidebar.js';
import { Header } from '../component/Header.js';

const DaftarBuku = () => {
    const { token, logout, isLogin } = useAuth();
    const [user, setUser] = useState({});
    const [searchQuery, setSearchQuery] = useState('');
    const [buku, setBuku] = useState([]);
    const [allBuku, setAllBuku] = useState([]); // Store all books

    const navigate = useNavigate();
    const location = useLocation();

    const searchQueryFromUrl = new URLSearchParams(location.search).get('q');


    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/user', {
                headers: {
                    Authorization: `Bearer ${token}`
                },
            });
            setUser(response.data);
        } catch (error) {
            console.error('Error fetching user:', error.message);
        }
    }, [token]);

    const fetchBuku = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/buku', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setBuku(response.data);
            setAllBuku(response.data); // Store all books
        } catch (error) {
            console.error('Error fetching books:', error.message);
        }
    }, [token]);

    const seachBuku = async (e) => {
        e.preventDefault();
        navigate(`/daftar-buku?q=${searchQuery}`);

        // Filter books based on search query
        const filteredBuku = allBuku.filter(book =>
            book.Judul.toLowerCase().includes(searchQuery.toLowerCase()) ||
            book.Penulis.toLowerCase().includes(searchQuery.toLowerCase()) ||
            book.Penerbit.toLowerCase().includes(searchQuery.toLowerCase())
        );
        setBuku(filteredBuku);
    };

    const deleteBuku = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/buku/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                },
            });
            fetchBuku();
        } catch (error) {
            console.error('Error deleting book:', error.message);
        }
    };

    useEffect(() => {
        if (!isLogin) {
            navigate('/login');
        } else {
            fetchUser();
            fetchBuku();
            if (searchQueryFromUrl) {
                setSearchQuery(searchQueryFromUrl);
            }
        }
    }, [isLogin, navigate, fetchUser, fetchBuku, searchQueryFromUrl]);



    return (
        <div className='flex flex-row h-screen w-screen '>
            <Sidebar />
            <div className="flex flex-col overflow-y-auto flex-1 bg-blue-100">
                <Header userp/>
                <div className="flex-1 flex-col m-6 p-4 rounded-lg shadow-md bg-gray-50">
                    <div className='flex flex-row items-center justify-between w-full gap-10  rounded-lg'>
                        <Link to="/add-buku-admin" className='bg-blue-500 text-white px-4 py-2 rounded'>Tambah Buku</Link>
                        <form className='' onSubmit={seachBuku}>
                            <input
                                type="text"
                                placeholder="Search"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="border p-2 rounded "
                            />
                            <button type="submit" className="bg-blue-500 text-white  px-4 py-2 rounded">
                                Search
                            </button>
                        </form>
                    </div>
                    {buku.length > 0 ? (
                        <table className="min-w-full divide-y divide-gray-200 ">
                            <thead className="bg-blue-200">
                                <tr>
                                    <th className="text-xs font-medium uppercase tracking-wider px-4 py-2 ">BukuID</th>
                                    <th className="text-xs font-medium uppercase tracking-wider px-4 py-2">Judul</th>
                                    <th className="text-xs font-medium uppercase tracking-wider px-4 py-2">Penulis</th>
                                    <th className="text-xs font-medium uppercase tracking-wider px-4 py-2 ">Penerbit</th>
                                    <th className="text-xs font-medium uppercase tracking-wider px-4 py-2 ">Tahun</th>
                                    <th className="text-xs font-medium uppercase tracking-wider px-4 py-2 ">Kategori</th>
                                    <th className="text-xs font-medium uppercase tracking-wider px-4 py-2 ">Stok</th>
                                    <th className="text-xs font-medium uppercase tracking-wider px-4 py-2 "></th>
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {buku.map((book) => (
                                    <tr key={book.BukuID}>
                                        <td className="px-4 py-2 whitespace-nowrap">{book.BukuID}</td>
                                        <td className="px-4 py-2 whitespace-nowrap">{book.Judul}</td>
                                        <td className="px-4 py-2 whitespace-nowrap">{book.Penulis}</td>
                                        <td className="px-4 py-2 whitespace-nowrap">{book.Penerbit}</td>
                                        <td className="px-4 py-2 whitespace-nowrap">{book.TahunTerbit}</td>
                                        <td className="px-4 py-2 whitespace-nowrap">
                                            {book.kategoribuku_relasis.length > 0
                                                ? book.kategoribuku_relasis.map((relasi, index) => (
                                                    <span key={index}>{relasi.Kategori.NamaKategori}{index < book.kategoribuku_relasis.length - 1 && ', '}</span>
                                                ))
                                                : 'No Category'}
                                        </td>
                                        <td className="px-4 py-2 whitespace-nowrap">{book.Stok}</td>
                                        <td className='whitespace-nowrap px-4 py-2'>
                                            <button className='py-1 px-2 bg-yellow-500 text-white rounded-lg'>Edit</button>
                                            <button className='py-1 px-2 bg-red-500 text-white rounded-lg' onClick={() => deleteBuku(book.BukuID)}>Hapus</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p className="text-gray-600 text-center">Tidak ada data buku tersedia.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default DaftarBuku;
