import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Admin } from '../lib/navigation';
import logo from '../imag/logo.png';
import  classNames  from 'classnames';

export const Sidebar = () => {
    return (
        <div className="hidden md:flex md:flex-col text-white shadow-[0_3px_6px_3px_rgba(0,0,0,0.3)] h-screen w-64 bg-gradient-to-br from-blue-500 to-purple-600 z-10">
            <img src={logo} alt="logo" className="h-1 mx-auto mt-5"></img>
            <div className="flex flex-col justify-center w-full mt-20 mb-5 h-2 bg-white">
                <div className="flex-1 flex-col h-8 p-5">
                    {Admin.map((item) => (
                        <SidebarAnchor key={item.key} item={item} />
                    ))}
                </div>
            </div>
        </div>
    );
};

function SidebarAnchor({ item }) {
    const {pathname} = useLocation();
    return (
        <Link to={item.path} className={classNames("flex flex-row items-center p-4 mt-4  hover:bg-gray-200", {"bg-gray-200 rounded-lg bg-opacity-55": pathname === item.path})}>
      <img src={item.icon} alt={item.label} className={"w-6 h-6"}/>
      <span className={classNames("ml-4 font-semibold text-xl", {"text-white": pathname === item.path})} >{item.label}</span>
    </Link>
  );
}
