import React, { useCallback, useEffect, useState } from 'react';
import axios, { Axios } from 'axios';
import { useAuth } from '../context/auth.js';
import { Sidebar } from '../component/Sidebar.js';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
    const { token, isLogin, isRole, logout } = useAuth();
    const [user, setUser] = useState({});
    const [buku, setBuku] = useState([]);
    const [countUser, setCountUser] = useState(0);
    const [countBorrow, setCountBorrow] = useState(0);
    const [countBuku, setCountBuku] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        if (!isLogin) {
            navigate('/login');
        } else if (isRole !== 'Admin') {
            navigate('/login'); // Arahkan ke login jika bukan admin
        }
    }, [isLogin, isRole, navigate]);

    const countuser = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/countuser', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setCountUser(response.data.count || 0); // Perbaikan: Ambil properti count dengan fallback
        } catch (error) {
            console.error('Error count user:', error.message);
        }
    };

    const countborrow = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/countborrow', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setCountBorrow(response.data.count || 0); // Perbaikan: Ambil properti count dengan fallback
        } catch (error) {
            console.error('Error count borrow:', error.message);
        }
    }

    const countbuku = async () => {
        try {
            const respons = await Axios.get('http://localhost:5000/api/countbuku', {
                
            })
        } catch (error) {
            
        }
    }

    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/user', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setUser(response.data);
        } catch (error) {
            console.error('Error fetching user:', error.message);
        }
    }, [token]);

    const fetchBuku = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/buku', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setBuku(response.data);
        } catch (error) {
            console.error('Error fetching books:', error.message);
        }
    }, [token]);

    useEffect(() => {
        fetchUser();
        fetchBuku();
        countuser();
        countborrow();
    }, [fetchUser, fetchBuku]);

    return (
        <div className='flex flex-row h-screen w-screen'>
            <Sidebar />
            <div className="flex flex-col flex-1 bg-gray-50">
                <div className="flex items-center justify-between rounded-lg m-6 p-4 bg-white shadow-md">
                    <div className="flex flex-col text-2xl font-semibold text-gray-800">
                        {user?.Username || 'Loading...'} {/* Perbaikan: Optional chaining */}
                        <span className='text-gray-500 text-sm'>{user?.Email || ''}</span>
                    </div>
                    <button onClick={logout} className="px-4 py-2 bg-red-500 text-white rounded">
                        Logout
                    </button>
                </div>

                <div className="flex flex-col p-6 gap-4 ">
                    <div className="flex items-center justify-between bg-white shadow rounded-lg p-4">
                        <p className="text-gray-800 font-medium">Jumlah Pengguna: {countUser}</p>
                    </div>
                    <div className="flex items-center justify-between bg-white shadow rounded-lg p-4">
                        <p className="text-gray-800 font-medium">Jumlah Peminjaman: {countBorrow}</p>
                    </div>

                    <div className="flex-1 bg-white shadow rounded-lg overflow-y-auto">
                        {buku.length > 0 ? (
                            <table className="table-fixed w-full">
                                <thead>
                                    <tr>
                                        <th className="px-4 py-2">Judul</th>
                                        <th className="px-4 py-2">Pengarang</th>
                                        <th className="px-4 py-2">Tahun</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {buku.map((book) => (
                                        <tr key={book.ID}>
                                            <td className="border px-4 py-2">{book.Judul}</td>
                                            <td className="border px-4 py-2">{book.Penulis}</td>
                                            <td className="border px-4 py-2">{book.TahunTerbit}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        ) : (
                            <p className="text-gray-600 text-center p-4">
                                Tidak ada data buku tersedia.
                            </p>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
