import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [Username, setUsername] = useState('');
  const [Password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isValid, setIsValid] = useState(null);
  const navigate = useNavigate();

  const handleValidation = async () => {
    console.log({ Username, Password });
    
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        Username,
        Password,
      },
      {
        headers: {
          'Content-Type': 'application/json',}
      }
    );
    const Dta = response.data;
    setIsValid(true);
    if (!!Dta) {
      navigate('/dashboard');
    } else {
      setIsValid(false);
    }    
    } catch (error) {
      console.error('Error during login:', error);
    }
    
  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleShowPasswordChange = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-200 to-blue-500">
    <div className="bg-white rounded-lg shadow-lg p-8 w-96">
      <h2 className="text-2xl font-bold mb-6 text-center">Selamat Datang Di Perpustakaan Digital</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="username" className="block text-gray-700 text-sm font-bold mb-2">
            Username
          </label>
          <input
            type="text"
            id="username"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            value={Username}
            onChange={handleUsernameChange}
          />
        </div>
        <div className="mb-4">
          <label htmlFor="password" className="block text-gray-700 text-sm font-bold mb-2">
            Password
          </label>
          <input
            type={showPassword ? 'text' : 'password'}
            id="password"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow -outline"
            value={Password}
            onChange={handlePasswordChange}
          />
          <div className="flex items-center mt-2">
            <input
              type="checkbox"
              id="showPassword"
              className="mr-2"
              checked={showPassword}
              onChange={handleShowPasswordChange}
            />
            <label htmlFor="showPassword" className="text-gray-600 text-sm">
              Tampilkan sandi
            </label>
          </div>
        </div>
        <div className="flex items-center justify-between">
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            MASUK
          </button>
        </div>
      </form>
      <p className="mt-4 text-center text-gray-600">
        Belum punya akun? <a href="#" className="text-blue-500 hover:underline">Sini daftar akun</a>
      </p>
    </div>
  </div>
);
}
}
export default Login;
