import React, { useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
import { Sidebar } from '../component/Sidebar.js';

const Dashboard = () => {
    const [Name, setName] = useState('');
    const [Email, setEmail] = useState('');
    const [Buku, setBuku] = useState([]);
    const [error, setError] = useState(null);

    const token = localStorage.getItem('token');

    // Decode token untuk mendapatkan UserID
    let UserID = null;
    if (token) {
        try {
            const decodedToken = jwt_decode(token);
            UserID = decodedToken.id; // Asumsi token punya payload dengan key `id`
        } catch (err) {
            setError('Token tidak valid.');
        }
    }

    // Fungsi logout
    const handleLogout = () => {
        localStorage.removeItem('token');
        window.location.href = '/login';
    };

    // Mengambil data profil pengguna
    const handleProfile = useCallback(async () => {
        if (!UserID || !token) {
            setError('User tidak ditemukan. Silakan login ulang.');
            return;
        }

        try {
            const response = await axios.get(`http://localhost:5000/api/user/${UserID}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });
            setName(response.data.Username);
            setEmail(response.data.Email);
        } catch (error) {
            console.error('Error fetching profile:', error);
            setError('Gagal memuat profil pengguna.');
        }
    }, [UserID, token]);

    // Mengambil data buku
    const handleBuku = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/buku', {
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });
            setBuku(response.data);
        } catch (error) {
            console.error('Error fetching books:', error);
            setError('Gagal memuat data buku.');
        }
    }, [token]);

    // Memuat data ketika komponen pertama kali dirender
    useEffect(() => {
        handleProfile();
        handleBuku();
    }, [handleProfile, handleBuku]);

    return (
        <div className="flex flex-row h-screen w-screen">
            <Sidebar />
            <div className="flex flex-col flex-1 bg-blue-100">
                {/* Header */}
                <div className="flex items-center justify-between rounded-lg m-6 p-4 bg-white shadow-md">
                    <div>
                        <h1 className="text-2xl font-semibold text-gray-800">
                            Selamat datang, {Name || 'Pengguna'}
                        </h1>
                        <p className="text-gray-600">{Email || 'Email tidak ditemukan.'}</p>
                    </div>
                    <button
                        onClick={handleLogout}
                        className="px-4 py-2 bg-red-500 text-white font-semibold rounded-lg shadow hover:bg-red-600"
                    >
                        Logout
                    </button>
                </div>

                {/* Konten Buku */}
                <div className="flex-1 p-6 bg-gray-50">
                    <h2 className="text-xl font-bold mb-4">Daftar Buku</h2>
                    {error && <p className="text-red-500">{error}</p>}
                    <ul className="space-y-4">
                        {Buku.length > 0 ? (
                            Buku.map((buku) => (
                                <li
                                    key={buku.id}
                                    className="p-4 bg-white rounded-lg shadow-md flex justify-between"
                                >
                                    <span>{buku.title}</span>
                                    <span className="text-gray-600">{buku.author}</span>
                                </li>
                            ))
                        ) : (
                            <p className="text-gray-500">Tidak ada buku tersedia.</p>
                        )}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
