import React, { useState } from "react";

export const GenerateLaporan = ({ onGenerate }) => {
  const [bulan, setBulan] = useState("");
  const [tahun, setTahun] = useState("");

  const bulanOptions = [
    "Januari", "Februari", "Maret", "April", "Mei", "Juni",
    "Juli", "Agustus", "September", "Oktober", "November", "Desember"
  ];

  const tahunOptions = Array.from({ length: 10 }, (_, i) => new Date().getFullYear() - i);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!bulan || !tahun) {
      alert("Pilih bulan dan tahun terlebih dahulu!");
      return;
    }
    onGenerate(tahun, bulan);
  };

  return (
    <div className="flex flex-col items-center justify-center p-6 bg-white rounded-lg shadow-md w-96">
      <h2 className="text-xl font-bold mb-4">Generate Laporan</h2>
      <form onSubmit={handleSubmit} className="w-full">
        <div className="mb-4">
          <label className="block text-sm font-medium">Pilih Bulan</label>
          <select
            value={bulan}
            onChange={(e) => setBulan(e.target.value)}
            className="w-full border p-2 rounded"
            required
          >
            <option value="">-- Pilih Bulan --</option>
            {bulanOptions.map((b, index) => (
              <option key={index} value={index + 1}>{b}</option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium">Pilih Tahun</label>
          <select
            value={tahun}
            onChange={(e) => setTahun(e.target.value)}
            className="w-full border p-2 rounded"
            required
          >
            <option value="">-- Pilih Tahun --</option>
            {tahunOptions.map((t, index) => (
              <option key={index} value={t}>{t}</option>
            ))}
          </select>
        </div>
        <div className="flex justify-end">
          <button
            type="submit"
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Generate Laporan
          </button>
        </div>
      </form>
    </div>
  );
};
