import React, { useState } from 'react';

export const AddBukuPopup = ({ onClose, onSave }) => {
    const [formData, setFormData] = useState({
        Judul: "",
        Penerbit: "",
        Penulis: "",
        TahunTerbit: "",
        Kategori: "",
        Stok: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(formData); // Mengirim data buku baru
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50" onClick={onClose}>
            <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-lg" onClick={(e) => e.stopPropagation()}>
                <h2 className="text-xl font-bold mb-4">Tambah Buku</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Judul</label>
                        <input
                            type="text"
                            name="Judul"
                            value={formData.Judul}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                            required
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Penerbit</label>
                        <input
                            type="text"
                            name="Penerbit"
                            value={formData.Penerbit}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                            required
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Penulis</label>
                        <input
                            type="text"
                            name="Penulis"
                            value={formData.Penulis}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                            required
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Tahun Terbit</label>
                        <input
                            type="number"
                            name="TahunTerbit"
                            value={formData.TahunTerbit}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                            required
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Kategori</label>
                        <input
                            type="text"
                            name="Kategori"
                            value={formData.Kategori}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                            
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Stok</label>
                        <input
                            type="number"
                            name="Stok"
                            value={formData.Stok}
                            onChange={handleChange}
                            className="mt-1 p-2 w-full border rounded-lg"
                            required
                        />
                    </div>
                    <div className="flex justify-end gap-2">
                        <button
                            type="button"
                            onClick={onClose}
                            className="bg-gray-500 text-white px-4 py-2 rounded-lg"
                        >
                            Batal
                        </button>
                        <button
                            type="submit"
                            className="bg-blue-500 text-white px-4 py-2 rounded-lg"
                        >
                            Tambah
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};
