import React, { useCallback, useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router";
import { Sidebar } from "../component/Sidebar";
import { Header } from "../component/Header";
import { EditUserPopup } from "../component/EditUserPopup";

const AkunUser = () => {
    const { token, logout, isLogin } = useAuth();
    const [users, setUsers] = useState([]);
    const [error, setError] = useState(null);
    const [selectedUser, setSelectedUser] = useState(null);
    const navigate = useNavigate();

    // Fetch all users
    const fetchUsers = useCallback(async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/users", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setUsers(response.data);
        } catch (error) {
            setError(error.message);
        }
    }, [token]);

    // Delete a user
    const deleteUser = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/users/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            fetchUsers();
        } catch (error) {
            setError(error.message);
        }
    };

    // Save updated user data
    const saveUser = async (id, updatedData) => {
        try {
            await axios.put(`http://localhost:5000/api/users/${id}`, updatedData, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setSelectedUser(null);
            fetchUsers();
        } catch (error) {
            setError(error.message);
        }
    };

    useEffect(() => {
        if (!isLogin) {
            navigate("/login");
            return;
        }
        fetchUsers();
    }, [fetchUsers, navigate, isLogin]);

    return (
        <div className="flex flex-row bg-gray-50">
            <Sidebar />
            <div className="flex flex-1 flex-col h-screen">
                <Header logout={logout} />

                <div className="m-6 bg-white rounded-lg shadow-md max-w-full h-full overflow-hidden">
                    {error && <p className="text-red-500 text-center">Error: {error}</p>}
                    <div className="overflow-x-auto max-h-full">
                        <table className="min-w-full divide-y divide-gray-200">
                            <thead className="bg-blue-500 sticky top-0">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">No</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">Nama</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">Email</th>
                                    <th className="px-6 py-3 text-center text-xs font-medium text-white uppercase tracking-wider">Aksi</th>
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {users.length > 0 ? (
                                    users.map((user, index) => (
                                        <tr key={user.id}>
                                            <td className="px-6 py-3 whitespace-nowrap text-sm">{index + 1}</td>
                                            <td className="px-6 py-3 whitespace-nowrap text-sm">{user.name}</td>
                                            <td className="px-6 py-3 whitespace-nowrap text-sm">{user.email}</td>
                                            <td className="items-center justify-center">
                                                <div className="gap-2 flex flex-row">
                                                    <button className="py-1 px-2 border-2 border-yellow-500 text-sm text-yellow-500 rounded-xl" onClick={() => setSelectedUser(user)}>Edit</button>
                                                    <button className="py-1 px-2 border-2 border-red-500 text-sm text-red-500 rounded-xl" onClick={() => deleteUser(user.id)}>Hapus</button>
                                                </div>
                                            </td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr>
                                        <td colSpan="4" className="text-center py-3 text-gray-500">Tidak ada data pengguna.</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>

                {selectedUser && (
                    <EditUserPopup
                        user={selectedUser}
                        onClose={() => setSelectedUser(null)}
                        onSave={saveUser}
                    />
                )}
            </div>
        </div>
    );
};

export default AkunUser;
