import React, { useCallback, useEffect, useState } from "react";
import axios from "axios";
import { Sidebar } from "../component/Sidebar";
import { Header } from "../component/Header";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router";

const DaftarUser = () => {
    const { token, logout, isLogin, isRole } = useAuth();
    const [user, setUser] = useState({});
    const [users, setUsers] = useState([]);
    const [error, setError] = useState("");
    const navigate = useNavigate();

    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/user", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setUser(response.data);
        } catch (error) {
            setError(error.message);
        }
    }, [token]);

    const getUsers = useCallback(async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/alluser", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setUsers(response.data);
        } catch (error) {
            setError(error.message);
        }
    }, [token]);

    const deleteUser = async (id) => {
        try {
            const response = await axios.delete(`http://localhost:5000/api/user/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            getUsers(response.data);
        } catch (error) {
            setError(error.message);
        }
    };

    useEffect(() => {
        if (!isLogin || isRole !== "Admin") {
            return navigate("/login");
        }
            fetchUser();
            getUsers();
    }, [fetchUser, getUsers,navigate, isLogin, isRole]);

    return (
        <div className="flex flex-row bg-gray-50">
            <Sidebar />
            <div className="flex-1 flex-col ">
                <Header user={user} logout={logout} />
                <div className="m-6 bg-white rounded-lg shadow-md max-w-full h-full overflow-h">
                    {users.length > 0 ? (
                        <table className="min-w-full divide-y divide-gray-200">
                            <thead className="bg-blue-500 sticky top-0">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        No
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Nama
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Email
                                    </th>
                                    <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Aksi
                                    </th>
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {users.map((user, index) => (
                                    <tr key={user.id}>
                                        <td className="px-6 py-2 whitespace-nowrap">
                                            {index + 1}
                                        </td>
                                        <td className="px-6 py-2 whitespace-nowrap">
                                            {user.Username}
                                        </td>
                                        <td className="px-6 py-2 whitespace-nowrap">
                                            {user.Email}
                                        </td>
                                        <td className="px-6 py-2 gap-2 flex flex-row justify-center  whitespace-nowrap">
                                            <button className='py-2 px-4 bg-yellow-500 text-white rounded-lg'>Edit</button>
                                            <button className='py-2 px-4 bg-red-500 text-white rounded-lg' onClick={() => deleteUser(user.UserID)}>Hapus</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>Tidak ada data user.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default DaftarUser;
