import React, { useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/auth.js';
import { Sidebar } from '../component/Sidebar.js';

const DaftarBuku = () => {
    const { token, logout, isLogin } = useAuth();
    const [user, setUser] = useState({});
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('judul'); // Default search by title
    const [selectedGenre, setSelectedGenre] = useState(''); // For genre filter
    const [buku, setBuku] = useState([]);
    const [allBuku, setAllBuku] = useState([]); // Store all books
    const [genres, setGenres] = useState([]); // Store available genres

    const navigate = useNavigate();
    const location = useLocation();

    const searchQueryFromUrl = new URLSearchParams(location.search).get('q');

    useEffect(() => {
        if (searchQueryFromUrl) {
            setSearchQuery(searchQueryFromUrl);
        }
    }, [searchQueryFromUrl]);

    useEffect(() => {
        if (!isLogin) {
            navigate('/login');
        }
    }, [isLogin, navigate]);

    const fetchUser = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/user', {
                headers: {
                    Authorization: `Bearer ${token}`
                },
            });
            setUser(response.data);
        } catch (error) {
            console.error('Error fetching user:', error.message);
        }
    }, [token]);

    const fetchBuku = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/buku', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setBuku(response.data);
            setAllBuku(response.data); // Store all books
        } catch (error) {
            console.error('Error fetching books:', error.message);
        }
    }, [token]);

    const fetchGenres = useCallback(async () => {
        try {
            // Assuming your backend has a genre endpoint
            const response = await axios.get('http://localhost:5000/api/genres', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setGenres(response.data); // Assuming response contains a list of genres
        } catch (error) {
            console.error('Error fetching genres:', error.message);
        }
    }, [token]);

    useEffect(() => {
        if (isLogin) {
            fetchUser();
            fetchBuku();
            fetchGenres(); // Fetch genres for the filter
        }
    }, [isLogin, fetchUser, fetchBuku, fetchGenres]);

    const searchAndFilterBuku = async (e) => {
        e.preventDefault();
        navigate(`/daftar-buku?q=${searchQuery}`);

        // Filter books based on search query and selected genre
        let filteredBuku = allBuku;

        // Apply search query filter
        if (searchQuery) {
            filteredBuku = filteredBuku.filter(book =>
                book[selectedCategory].toLowerCase().includes(searchQuery.toLowerCase())
            );
        }

        // Apply genre filter
        if (selectedGenre) {
            filteredBuku = filteredBuku.filter(book => book.Genre.toLowerCase().includes(selectedGenre.toLowerCase()));
        }

        setBuku(filteredBuku);
    };

    const deleteBuku = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/buku/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                },
            });
            fetchBuku(); // Re-fetch all books after deletion
        } catch (error) {
            console.error('Error deleting book:', error.message);
        }
    };

    return (
        <div className='flex flex-row h-screen w-screen '>
            <Sidebar />
            <div className="flex flex-col overflow-y-auto flex-1 bg-blue-100">
                <div className="flex items-center justify-between rounded-lg m-4 p-4 bg-white shadow-md">
                    <button onClick={logout} className="px-4 py-2 bg-red-500 text-white rounded-lg">
                        Logout
                    </button>
                </div>
                <div className="flex-1 flex-col p-6 m-4 rounded-lg shadow-md bg-gray-50">
                    <div className='flex flex-row items-center justify-between w-full gap-10 text-white rounded-lg'>
                        <Link to="/add-buku" className='bg-blue-500 text-white px-4 py-2 rounded'>Tambah Buku</Link>
                        <form onSubmit={searchAndFilterBuku} className="flex gap-4 items-center">
         
                                type="text"
                                placeholder="Search"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="border p-2 rounded"
                            />
                            <select
                                value={selectedGenre}
                                onChange={(e) => setSelectedGenre(e.target.value)}
                                className="border p-2 rounded"
                            >
                                <option value="">All Genres</option>
                                {genres.map((genre) => (
                                    <option key={genre} value={genre}>{genre}</option>
                                ))}
                            </select>
                            <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
                                Search & Filter
                            </button>
                        </form>
                    </div>
                    {buku.length > 0 ? (
                        <table className="table-none table-fixed mt-10 w-full">
                            <thead className="bg-gray-200 rounded-lg">
                                <tr>
                                    <th className="text-lg border-gray-600 border px-4 py-2 ">BukuID</th>
                                    <th className="text-lg border-gray-600 border px-4 py-2">Judul</th>
                                    <th className="text-lg border-gray-600 border px-4 py-2">Penulis</th>
                                    <th className="text-lg border-gray-600 border px-4 py-2 ">Penerbit</th>
                                    <th className="text-lg border-gray-600 border px-4 py-2 ">Tahun</th>
                                    <th className="text-lg border-gray-600 border px-4 py-2 "></th>
                                    <th className="text-lg border-gray-600 border px-4 py-2 "></th>
                                </tr>
                            </thead>
                            <tbody>
                                {buku.map((book) => (
                                    <tr key={book.BukuID}>
                                        <td className="border-gray-600 border px-4 py-2">{book.BukuID}</td>
                                        <td className="border-gray-600 border px-4 py-2">{book.Judul}</td>
                                        <td className="border-gray-600 border px-4 py-2">{book.Penulis}</td>
                                        <td className="border-gray-600 border px-4 py-2">{book.Penerbit}</td>
                                        <td className="border-gray-600 border px-4 py-2">{book.TahunTerbit}</td>
                                        <td className='border-gray-600 border text-l font-semibold px-4 py-2'>
                                            <button className='py-2 px-4 bg-yellow-500 text-white rounded-lg'>Edit</button>
                                        </td>
                                        <td className='border-gray-600 border text-l font-semibold px-4 py-2'>
                                            <button className='py-2 px-4 bg-red-500 text-white rounded-lg' onClick={() => deleteBuku(book.BukuID)}>Hapus</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p className="text-gray-600 text-center">Tidak ada data buku tersedia.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default DaftarBuku;
